    99‚Üí<body>
   100‚Üí    <h1>üèÉ Maze Escape</h1>
   101‚Üí    
   102‚Üí    <div id="game-container">
   103‚Üí        <div id="stats">
   104‚Üí            <div class="stat">Moves: <span id="moves" class="stat-value">0</span></div>
   105‚Üí            <div class="stat">Time: <span id="time" class="stat-value">0:00</span></div>
   106‚Üí            <div class="stat">Level: <span id="level" class="stat-value">1</span></div>
   107‚Üí        </div>
   108‚Üí        
   109‚Üí        <canvas id="maze-canvas"></canvas>
   110‚Üí        
   111‚Üí        <div id="message"></div>
   112‚Üí        
   113‚Üí        <div id="controls">
   114‚Üí            <button id="new-game-btn">New Game</button>
   115‚Üí            <button id="next-level-btn" style="display: none;">Next Level ‚Üí</button>
   116‚Üí        </div>
   117‚Üí        
   118‚Üí        <div id="instructions">Use Arrow Keys or WASD to move</div>
   119‚Üí    </div>
   120‚Üí
   121‚Üí    <script>
   122‚Üí        // ==================== GAME CONFIGURATION ====================
   123‚Üí        const CONFIG = {
   124‚Üí            cellSize: 30,
   125‚Üí            baseSize: 11,      // Starting maze size (must be odd)
   126‚Üí            sizeIncrement: 2,  // How much maze grows per level
   127‚Üí            maxSize: 31,       // Maximum maze size
   128‚Üí            colors: {
   129‚Üí                wall: '#2c3e50',
   130‚Üí                path: '#ecf0f1',
   131‚Üí                player: '#e74c3c',
   132‚Üí                exit: '#27ae60',
   133‚Üí                visited: '#d5f4e6'
   134‚Üí            }
   135‚Üí        };
   136‚Üí
   137‚Üí        // ==================== GAME STATE ====================
   138‚Üí        const game = {
   139‚Üí            maze: [],
   140‚Üí            width: 0,
   141‚Üí            height: 0,
   142‚Üí            player: { x: 1, y: 1 },
   143‚Üí            exit: { x: 0, y: 0 },
   144‚Üí            moves: 0,
   145‚Üí            startTime: 0,
   146‚Üí            timerInterval: null,
   147‚Üí            level: 1,
   148‚Üí            won: false,
   149‚Üí            visited: new Set()
   150‚Üí        };
   151‚Üí
   152‚Üí        // ==================== DOM ELEMENTS ====================
   153‚Üí        const canvas = document.getElementById('maze-canvas');
   154‚Üí        const ctx = canvas.getContext('2d');
   155‚Üí        const movesEl = document.getElementById('moves');
   156‚Üí        const timeEl = document.getElementById('time');
   157‚Üí        const levelEl = document.getElementById('level');
   158‚Üí        const messageEl = document.getElementById('message');
   159‚Üí        const newGameBtn = document.getElementById('new-game-btn');
   160‚Üí        const nextLevelBtn = document.getElementById('next-level-btn');
   161‚Üí
   162‚Üí        // ==================== MAZE GENERATION ====================
   163‚Üí        function generateMaze(width, height) {
   164‚Üí            // Initialize maze with all walls
   165‚Üí            const maze = Array(height).fill(null).map(() => Array(width).fill(1));
   166‚Üí            
   167‚Üí            // Recursive backtracking algorithm
   168‚Üí            function carve(x, y) {
   169‚Üí                maze[y][x] = 0;
   170‚Üí                
   171‚Üí                // Directions: up, right, down, left
   172‚Üí                const directions = [
   173‚Üí                    { dx: 0, dy: -2 },
   174‚Üí                    { dx: 2, dy: 0 },
   175‚Üí                    { dx: 0, dy: 2 },
   176‚Üí                    { dx: -2, dy: 0 }
   177‚Üí                ];
   178‚Üí                
   179‚Üí                // Shuffle directions
   180‚Üí                for (let i = directions.length - 1; i > 0; i--) {
   181‚Üí                    const j = Math.floor(Math.random() * (i + 1));
   182‚Üí                    [directions[i], directions[j]] = [directions[j], directions[i]];
   183‚Üí                }
   184‚Üí                
   185‚Üí                for (const { dx, dy } of directions) {
   186‚Üí                    const nx = x + dx;
   187‚Üí                    const ny = y + dy;
   188‚Üí                    
   189‚Üí                    if (nx > 0 && nx < width - 1 && ny > 0 && ny < height - 1 && maze[ny][nx] === 1) {
   190‚Üí                        // Carve passage
   191‚Üí                        maze[y + dy / 2][x + dx / 2] = 0;
   192‚Üí                        carve(nx, ny);
   193‚Üí                    }
   194‚Üí                }
   195‚Üí            }
   196‚Üí            
   197‚Üí            // Start carving from (1, 1)
   198‚Üí            carve(1, 1);
   199‚Üí            
   200‚Üí            return maze;
   201‚Üí        }
   202‚Üí
   203‚Üí        // ==================== RENDERING ====================
   204‚Üí        function render() {
   205‚Üí            const { cellSize, colors } = CONFIG;
   206‚Üí            
   207‚Üí            // Clear canvas
   208‚Üí            ctx.fillStyle = colors.wall;
   209‚Üí            ctx.fillRect(0, 0, canvas.width, canvas.height);
   210‚Üí            
   211‚Üí            // Draw maze
   212‚Üí            for (let y = 0; y < game.height; y++) {
   213‚Üí                for (let x = 0; x < game.width; x++) {
   214‚Üí                    if (game.maze[y][x] === 0) {
   215‚Üí                        // Check if visited
   216‚Üí                        const isVisited = game.visited.has(`${x},${y}`);
   217‚Üí                        ctx.fillStyle = isVisited ? colors.visited : colors.path;
   218‚Üí                        ctx.fillRect(x * cellSize, y * cellSize, cellSize, cellSize);
   219‚Üí                    }
   220‚Üí                }
   221‚Üí            }
   222‚Üí            
   223‚Üí            // Draw exit
   224‚Üí            ctx.fillStyle = colors.exit;
   225‚Üí            ctx.fillRect(
   226‚Üí                game.exit.x * cellSize + 2,
   227‚Üí                game.exit.y * cellSize + 2,
   228‚Üí                cellSize - 4,
   229‚Üí                cellSize - 4
   230‚Üí            );
   231‚Üí            
   232‚Üí            // Draw exit symbol
   233‚Üí            ctx.fillStyle = '#fff';
   234‚Üí            ctx.font = `${cellSize * 0.6}px Arial`;
   235‚Üí            ctx.textAlign = 'center';
   236‚Üí            ctx.textBaseline = 'middle';
   237‚Üí            ctx.fillText('üö™', 
   238‚Üí                game.exit.x * cellSize + cellSize / 2,
   239‚Üí                game.exit.y * cellSize + cellSize / 2
   240‚Üí            );
   241‚Üí            
   242‚Üí            // Draw player
   243‚Üí            ctx.fillStyle = colors.player;
   244‚Üí            ctx.beginPath();
   245‚Üí            ctx.arc(
   246‚Üí                game.player.x * cellSize + cellSize / 2,
   247‚Üí                game.player.y * cellSize + cellSize / 2,
   248‚Üí                cellSize / 2 - 4,
   249‚Üí                0,
   250‚Üí                Math.PI * 2
   251‚Üí            );
   252‚Üí            ctx.fill();
   253‚Üí            
   254‚Üí            // Draw player face
   255‚Üí            ctx.fillStyle = '#fff';
   256‚Üí            ctx.font = `${cellSize * 0.5}px Arial`;
   257‚Üí            ctx.fillText('üòä',
   258‚Üí                game.player.x * cellSize + cellSize / 2,
   259‚Üí                game.player.y * cellSize + cellSize / 2
   260‚Üí            );
   261‚Üí        }
   262‚Üí
   263‚Üí        // ==================== GAME LOGIC ====================
   264‚Üí        function movePlayer(dx, dy) {
   265‚Üí            if (game.won) return;
   266‚Üí            
   267‚Üí            const newX = game.player.x + dx;
   268‚Üí            const newY = game.player.y + dy;
   269‚Üí            
   270‚Üí            // Check bounds and walls
   271‚Üí            if (newX >= 0 && newX < game.width && 
   272‚Üí                newY >= 0 && newY < game.height && 
   273‚Üí                game.maze[newY][newX] === 0) {
   274‚Üí                
   275‚Üí                game.player.x = newX;
   276‚Üí                game.player.y = newY;
   277‚Üí                game.moves++;
   278‚Üí                movesEl.textContent = game.moves;
   279‚Üí                
   280‚Üí                // Track visited cells
   281‚Üí                game.visited.add(`${newX},${newY}`);
   282‚Üí                
   283‚Üí                // Check win condition
   284‚Üí                if (newX === game.exit.x && newY === game.exit.y) {
   285‚Üí                    win();
   286‚Üí                }
   287‚Üí                
   288‚Üí                render();
   289‚Üí            }
   290‚Üí        }
   291‚Üí
   292‚Üí        function win() {
   293‚Üí            game.won = true;
   294‚Üí            clearInterval(game.timerInterval);
   295‚Üí            messageEl.textContent = `üéâ Level ${game.level} Complete! üéâ`;
   296‚Üí            nextLevelBtn.style.display = 'inline-block';
   297‚Üí        }
   298‚Üí
   299‚Üí        function updateTimer() {
   300‚Üí            const elapsed = Math.floor((Date.now() - game.startTime) / 1000);
   301‚Üí            const minutes = Math.floor(elapsed / 60);
   302‚Üí            const seconds = elapsed % 60;
   303‚Üí            timeEl.textContent = `${minutes}:${seconds.toString().padStart(2, '0')}`;
   304‚Üí        }
   305‚Üí
   306‚Üí        function startGame(level = 1) {
   307‚Üí            game.level = level;
   308‚Üí            game.won = false;
   309‚Üí            game.moves = 0;
   310‚Üí            game.visited.clear();
   311‚Üí            
   312‚Üí            // Calculate maze size based on level
   313‚Üí            const size = Math.min(
   314‚Üí                CONFIG.baseSize + (level - 1) * CONFIG.sizeIncrement,
   315‚Üí                CONFIG.maxSize
   316‚Üí            );
   317‚Üí            game.width = size;
   318‚Üí            game.height = size;
   319‚Üí            
   320‚Üí            // Generate maze
   321‚Üí            game.maze = generateMaze(game.width, game.height);
   322‚Üí            
   323‚Üí            // Set player start position
   324‚Üí            game.player = { x: 1, y: 1 };
   325‚Üí            game.visited.add('1,1');
   326‚Üí            
   327‚Üí            // Set exit position (bottom-right corner area)
   328‚Üí            game.exit = { x: game.width - 2, y: game.height - 2 };
   329‚Üí            
   330‚Üí            // Setup canvas
   331‚Üí            canvas.width = game.width * CONFIG.cellSize;
   332‚Üí            canvas.height = game.height * CONFIG.cellSize;
   333‚Üí            
   334‚Üí            // Reset UI
   335‚Üí            movesEl.textContent = '0';
   336‚Üí            levelEl.textContent = level;
   337‚Üí            messageEl.textContent = 'Find the exit! üö™';
   338‚Üí            nextLevelBtn.style.display = 'none';
   339‚Üí            
   340‚Üí            // Start timer
   341‚Üí            clearInterval(game.timerInterval);
   342‚Üí            game.startTime = Date.now();
   343‚Üí            game.timerInterval = setInterval(updateTimer, 1000);
   344‚Üí            updateTimer();
   345‚Üí            
   346‚Üí            render();
   347‚Üí        }
   348‚Üí
   349‚Üí        function nextLevel() {
   350‚Üí            startGame(game.level + 1);
   351‚Üí        }
   352‚Üí
   353‚Üí        // ==================== INPUT HANDLING ====================
   354‚Üí        document.addEventListener('keydown', (e) => {
   355‚Üí            switch (e.key) {
   356‚Üí                case 'ArrowUp':
   357‚Üí                case 'w':
   358‚Üí                case 'W':
   359‚Üí                    e.preventDefault();
   360‚Üí                    movePlayer(0, -1);
   361‚Üí                    break;
   362‚Üí                case 'ArrowDown':
   363‚Üí                case 's':
   364‚Üí                case 'S':
   365‚Üí                    e.preventDefault();
   366‚Üí                    movePlayer(0, 1);
   367‚Üí                    break;
   368‚Üí                case 'ArrowLeft':
   369‚Üí                case 'a':
   370‚Üí                case 'A':
   371‚Üí                    e.preventDefault();
   372‚Üí                    movePlayer(-1, 0);
   373‚Üí                    break;
   374‚Üí                case 'ArrowRight':
   375‚Üí                case 'd':
   376‚Üí                case 'D':
   377‚Üí                    e.preventDefault();
   378‚Üí                    movePlayer(1, 0);
   379‚Üí                    break;
   380‚Üí            }
   381‚Üí        });
   382‚Üí
   383‚Üí        // Button handlers
   384‚Üí        newGameBtn.addEventListener('click', () => startGame(1));
   385‚Üí        nextLevelBtn.addEventListener('click', nextLevel);
   386‚Üí
   387‚Üí        // ==================== START GAME ====================
   388‚Üí        startGame(1);
   389‚Üí    </script>
   390‚Üí</body>
